import React from 'react';
import './App.css';
import { BrowserRouter as Router, Switch, Route} from "react-router-dom"
//import Signup from './components/Signup'
import Login from './components/Login'
import Navigation from './Navigation'
import Tweets from './components/Tweets'
import Profile from './components/Profile'
import Signup from './components/Signup';
//import ProtectedRoutes from './components/ProtectedRoutes'
class App extends React.Component {
  constructor() {
    super()
    this.state = {
      loggedInStatus: "Not_logged_in",
      user: {}
    }
  }

  render() {

    return (

      <div className="App">
        <Router>
        <Navigation />
        <Switch>
          <Route exact path="/"
          render = {props => (
            <Login {...props} loggedInStatus={this.state.loggedInStatus}/>
          )}/>
          <Route path="/register" component={Signup}/>
          {/* <ProtectedRoutes path="/profile" component={Profile} exact/> */}
          {/* <Route path="/login" component={Login} exact/> */}
          {/* <Route path="/tweets" render={(props) => <Tweets {...props}/>}/> */}
          <Route exact path="/profile" 
          render={props => (
            <Profile {...props} loggedInStatus={this.state.loggedInStatus} />
          )}/>
          <Route exact path="/tweets" 
          render={props => (
            <Tweets {...props} loggedInStatus={this.state.loggedInStatus} />
          )}/>
        </Switch>
      </Router>


      </div>


    )

  }


}

export default App;
