import React from 'react'
import {Card,Form,Button,Col, Row, Container} from "react-bootstrap"

export default class Profile extends React.Component {
    constructor() {
        super()
        this.state = {
            id: "",
            user: [],
            username: "",
            bio: ""
        }
    }
    // user id as props from login
    // hämta user info och möjliggör edit
    componentDidMount(props){
        // id från login
        console.log("id: ",this.props.location.state.id)
        this.setState ({id: this.props.location.state.id})
        fetch("http://localhost:8080/users/all")
        .then(res => res.json())
        .then((result) => {
            for(var i = 0; i < result.length; i++) {
                if(result[i].id === this.state.id) {
                    this.setState ({user: result[i]})
                }
            }
            console.log(this.state.user)
        })
    }

    render() {

        return(
            <div>
                <Container>
                    <Row>
                      <Col></Col>
                      <Col>
                        <h2>hej {this.state.user.username}</h2>
                        <p>Change bio:</p>
                      </Col>
                      <Col></Col>  
                    </Row>
                </Container>
                
            </div>
        )
    }
}
