import React from 'react';
import './App.css';
import { Container, Row, Col } from 'react-bootstrap'

class App extends React.Component {

  state = {
    isLoading: true,
    users: []
  }

  async componentDidMount() {
    const response = await fetch('/users/all')
    const body = await response.json()
    this.setState({
      groups: body,
      isLoading: false
    })
  }

  render() {

    return (
      <div className="App">
        <Container>
