import React from 'react';
import './App.css';
import { Container, Row, Col } from 'react-bootstrap'

class App extends React.Component {

  state = {
    isLoading: true,
    users: []
  }

  async componentDidMount() {
    const response = await fetch('/users/all')
    const body = await response.json()
    this.setState({
      users: body,
      isLoading: false
    })
  }

  render() {
    const { users, isLoading} = this.state
    if(isLoading) {
      return <p>Loading..</p>
    }

    return (
      <div className="App">
        <Container>
          <Row>
            <Col>Menu</Col>
            <Col>
            <h2>welcome to fakeTwitter</h2>
            <h3>Login:</h3>
            or
            <h3>Sign up</h3>

            {users.map(user =>
              <div key= {user.d}>
                {user.name}
              </div>)}

            <hr/>
            twitter stream if logged in</Col>
            <Col></Col>
          </Row>
        </Container>

      </div>

    )

  }


}

export default App;
