import React from 'react'
import { Redirect } from 'react-router-dom'
import {Card,Form,Button,Col, Row, Container} from "react-bootstrap"
import Signup from './Signup'
import $ from 'jquery'

export default class Login extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            users: [],
            username: "",
            password: "",
            id: "",
            isLoggedIn: false,
            redirect: null
        }
    }

    componentDidMount = () => {
        fetch("http://localhost:8080/users/all")
        .then(res => res.json())
        .then((result) => {
            this.setState ({ users: result })
        })
    }

/*     renderRedirect = () => {
        console.log("rendering")
          return <Redirect to={{
            pathname: '/tweets',
            state: {
                id: this.state.id,
                isLoggedIn: this.state.isLoggedIn
            }
        }} />
        
      } */

    // function(s) that serarches users for correct and matches password
    handleLogin = (e) => {
        console.log(this.state.users)
        e.preventDefault()
        var user = this.state.users[0].username
        var password = this.state.users[0].password
        var id = this.state.users[0].id
        var username = this.state.username
        var passwordInput = this.state.password
        this.setState({id: this.state.users[0].id})
        if (username === "" && passwordInput === "") {
            console.log("error")
            
        }
        else if (user === username && passwordInput === password) {
            console.log ("Login success: " + user, password, id)
            this.setState({
                isLoggedIn: true, 
                id: id,
                redirect: "/profile"})
        }
        else {
            console.log("Username/password not valid")
        }
    }

    register = () => {
        $(".register").css("display", "block")
      }

    render() {

        if (this.state.redirect) {
            return <Redirect to={{
                pathname: this.state.redirect,
                state: {
                    id: this.state.id,
                    isLoggedIn: this.state.isLoggedIn
                }
            }} />
        }

        return(
            <div>
        <Container>
          <Row>
            <Col></Col>
            <Col>
            <br/>
            <h2>welcome to fakeTwitter</h2>
            <br/>
            <Card className={"border border-dark bg-dark text-white"}>
                    <Card.Header><h3>Login</h3></Card.Header>
                    <Card.Body>
                            <Form.Row>
                                <Form.Group as= {Col}>
                                    <Form.Label>Username</Form.Label>
                                    <Form.Control required autoComplete="off"
                                    type="text"
                                    id = "username"
                                    name = "username"
                                    value={this.state.value}
                                    onChange= {e => this.setState({username: e.target.value})}
                                    className={"bg-dark text-white"}
                                    placeholder= "Username" />
                                    <Form.Label>Password</Form.Label>
                                    <Form.Control required autoComplete="off"
                                    type="text"
                                    id = "password"
                                    name = "password"
                                    value={this.state.value}
                                    onChange= {e => this.setState({password: e.target.value})}
                                    className={"bg-dark text-white"}
                                    placeholder= "Password" />
                                </Form.Group>
                            </Form.Row>
                            <Card.Footer style={{"textAlign":"right"}}>
                                <Button size="sm" variant="primary" onClick={this.handleLogin}>Login</Button>
                            </Card.Footer>
                        </Card.Body>
                    
                </Card>
            
            </Col>
            <Col></Col>
          </Row>
          </Container>
            
            </div>
    
        )
    }
}
