import React from 'react'
import axios from "axios"
import {Card,Form,Button,Col} from "react-bootstrap"
import {Link} from "react-router-dom"

export default class Signup extends React.Component {
    constructor(props) {
        super()
        this.state = this.initialState
        this.state.show = false
        this.userChange = this.userChange.bind(this)
        this.submitUser = this.submitUser.bind(this)
    }
    initialState = { 
        userName: "" 
    }

    resetUser = () => {
        this.setState(() => this.initialState)
    }
    submitUser = e => {
        e.preventDefault()
        const user = this.state.userName
        axios.post("http://localhost:8080/users", user)
        .then(response => {
            if(response.data!=null) {
                this.setState({ "show": true})
                setTimeout(() => this.setState({ "show": false}), 3000)
            }
            else{
                this.setState({ "show": false})
            }
        })
        this.setState(this.initialState)
    }
    userChange = e => {
        this.setState({ userName: e.target.value})
    }
    
    render() {
        return (
            <div>
                <div style= {{"display": this.state.show ? "block": "none"}}>
                </div>
                <Card className={"border border-dark bg-dark text-white"}>
                    <Card.Header>Register</Card.Header>
                    <Form onReset={this.resetUser} onSubmit={this.submitUser} id="userFormId">
                        <Card.Body>
                            <Form.Row>
                                <Form.Group as= {Col} controlId="userName">
                                    <Form.Label>Username</Form.Label>
                                    <Form.Control required autoComplete="off"
                                    type="test"
                                    name="username"
                                    value={this.state.userName}
                                    onChange={this.userChange}
                                    className={"bg-dark text-white"}
                                    placeholder= "Username" />
                                </Form.Group>
                            </Form.Row>
                            <Card.Footer style={{"textAlign":"right"}}>
                                <Button size="sm" variant="primary" type="submit">Submit</Button>
                                {" "}
                                <Button size="sm" variant="primary" type="reset">Reset</Button>
                            </Card.Footer>
                        </Card.Body>
                    </Form>
                    {/* <center><Link to="/login">Login</Link></center> */}
                </Card>
            </div>
        )
    }

}
