import React from 'react'
import {Card,Form,Button,Col, Row, Container} from "react-bootstrap"

export const Login = () => {

    const initialState = {
        users: [],
        username: "",
        password: "",
        isSubmiting: false,
        errorMsg: null
    }

    const [data, setData] = React.useState(initialState)

    const handleInputChange = e => {
        setData({
            ...data,
            [e.target.name]: e.target.value
        })
    }
    /* constructor(props) {
        super(props)
        this.handleSuccessfulAuth = this.handleSuccessfulAuth.bind(this)
        this.state = {
            users: [],
            username: "",
            password: "",
            id: "",
        }
    } */

/*     componentDidMount = () => {
        fetch("http://localhost:8080/users/all")
        .then(res => res.json())
        .then((res) => {
            this.setState ({ users: res })
        })
    } */

  /*   handleSuccessfulAuth(data) {
        //TODO update parent component app.js
        this.props.history.push("/tweets") //after login success
    } */



    // function(s) that serarches users for correct and matches password
    /* handleLogin = (e) => {

        console.log(this.state.users)
        e.preventDefault()
        var users = this.state.users
        var username = this.state.username
        var passwordInput = this.state.password

        var user = users.find(el => el.username === username && el.password === passwordInput)
        if (user === undefined) {
            console.log("error")
        }
        else {
            console.log(user)
            this.props.handleSuccessfulAuth(user.data)
        }
        
    } */



        return(
            <div>
        <Container>
          <Row>
            <Col></Col>
            <Col>
            <br/>
            <br/>
            <Card className={"border border-dark bg-dark text-white"}>
                    <Card.Header><h3>Login</h3></Card.Header>
                    <Card.Body>
                        <Form>
                            <Form.Row>
                                <Form.Group as= {Col}>
                                    <Form.Label>Username</Form.Label>
                                    <Form.Control required autoComplete="off"
                                    type="text"
                                    id = "username"
                                    name = "username"
                                    value={data.username}
                                    onChange= {handleInputChange}
                                    className={"bg-dark text-white"}
                                    placeholder= "Username" />
                                    <Form.Label>Password</Form.Label>
                                    <Form.Control required autoComplete="off"
                                    type="text"
                                    id = "password"
                                    name = "password"
                                    value={data.password}
                                    onChange= {handleInputChange}
                                    className={"bg-dark text-white"}
                                    placeholder= "Password" />
                                </Form.Group>
                            </Form.Row>
                            {data.errorMessage && (
              <span className="form-error">{data.errorMessage}</span>
            )}

            <button disabled={data.isSubmitting}>
              {data.isSubmitting ? (
                "Loading..."
              ) : (
                "Login"
              )}
            </button>
                            </Form>
                            {/* <Card.Footer style={{"textAlign":"right"}}>
                                <Button size="sm" variant="primary" onClick={this.handleLogin}>Login</Button>
                            </Card.Footer> */}
                        </Card.Body>
                    
                </Card>
            
            </Col>
            <Col></Col>
          </Row>
          </Container>
            
            </div>
    
        )
}
export default Login
