import React from 'react'
import {Card,Form,Button,Col} from "react-bootstrap"

export default class Login extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            users: [],
            username: "",
            password: ""
        }
    }

    componentDidMount = () => {
        fetch("http://localhost:8080/users/all")
        .then(res => res.json())
        .then((result) => {
            this.setState ({ users: result })
        })
    }

    // function(s) that serarches users for correct and matches password
    handleLogin = (e) => {
        console.log(this.state.users)
        e.preventDefault()
        var user = this.state.users.data[0].username
        var password = this.state.users.data[0].password
        var username = this.state.username
        var passwordInput = this.state.password
        if (username === "" && passwordInput === "") {
            console.log("error")
        }
        else if (user === username && passwordInput === password) {
            console.log ("Login success: " + user, password)
        }
        else {
            console.log("Username/password not valid")
        }
    }

    render() {
        return(
            <div>
                <Card className={"border border-dark bg-dark text-white"}>
                    <Card.Header><h3>Login</h3></Card.Header>
                    <Card.Body>
                            <Form.Row>
                                <Form.Group as= {Col}>
                                    <Form.Label>Username</Form.Label>
                                    <Form.Control required autoComplete="off"
                                    type="text"
                                    id = "username"
                                    name = "username"
                                    value={this.state.value}
                                    onChange= {e => this.setState({username: e.target.value})}
                                    className={"bg-dark text-white"}
                                    placeholder= "Username" />
                                    <Form.Label>Password</Form.Label>
                                    <Form.Control required autoComplete="off"
                                    type="text"
                                    id = "password"
                                    name = "password"
                                    value={this.state.value}
                                    onChange= {e => this.setState({password: e.target.value})}
                                    className={"bg-dark text-white"}
                                    placeholder= "Password" />
                                </Form.Group>
                            </Form.Row>
                            <Card.Footer style={{"textAlign":"right"}}>
                                <Button size="sm" variant="primary" onClick={this.handleLogin}>Login</Button>
                            </Card.Footer>
                        </Card.Body>
                    
                </Card>
            </div>
        )
    }
}
